<?php

namespace App\Controller;

use App\Entity\Pays;
use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;
use Symfony\Component\Routing\Annotation\Route;

/**
 * @Route("/{_locale}")
 */
class PaysController extends AbstractController
{
    /**
     * @Route("/pays", name="pays")
     */
    public function index()
    {
        return $this->render('pays/index.html.twig', [
            'controller_name' => 'PaysController',
        ]);
    }

    /**
     * @Route("/pays/add", name="add_pays")
     */
    public function addPays()
    {
        $entityManager = $this->getDoctrine()->getManager();
        $pays = new Pays();
        $pays->setName('France');
        $pays->setImage('data:image/jpeg;base64,');
        $pays->setPopulation(100000);

        $entityManager->persist($pays);
        $entityManager->flush();

        return $this->render('pays/pays-add.html.twig', [
            'pays' => $pays,
        ]);

    }
}
